import { useEffect, useState } from 'react';
import Button from "@/modules/shared/components/ui/Button";
import PhoneInput from "@/modules/shared/components/ui/PhoneInput";
import { Form, Formik } from "formik";
import { useTranslation } from "next-i18next";
import { registerOrLogin, sendOtp } from "../actions";
import { useAppDispatch } from '@/modules/shared/hooks/use-store';
import { setReduxError } from '@/modules/shared/store/errorSlice';
import ModalPortal from '@/modules/shared/components/ui/ModalPortal';
import Image from 'next/image';
import OtpInput from '@/modules/shared/components/ui/OtpInput';
import CountDown from '@/modules/shared/components/ui/CountDown';
import Skeleton from '@/modules/shared/components/ui/Skeleton';
import { setReduxUser } from '../store/authenticationSlice';
import { persianNumbersToEnglish } from '@/modules/shared/helpers';
import { Close } from '@/modules/shared/components/ui/icons';
import { setReduxNotification } from '@/modules/shared/store/notificationSlice';

type Props = {
    onBackToLoginWithPassword: () => void;
    onCloseLogin:() => void;
}

const OTPLogin: React.FC<Props> = props => {

    const { t } = useTranslation('common');
    const [loading, setLoading] = useState<boolean>(false);
    const [registerLoading, setRegisterLoading] = useState<boolean>(false);
    const [savedPhoneNumber, setSavedPhoneNumber] = useState<string>();
    const [showVerificationForm, setShowVerificationForm] = useState<boolean>(false);
    const [showVerificationFormDelayed, setShowVerificationFormDelayed] = useState<boolean>(false);
    const [sendCodeMoment, setSendCodeMoment] = useState<number>(new Date().getTime());
    const [remaindSeconds, setRemaindSeconds] = useState<number>(80);
    const [enteredCode, setEnteredCode] = useState<string>("");

    useEffect(() => {

        let countDownTimer: any;

        if (sendCodeMoment) {
            countDownTimer = setInterval(() => {
                setRemaindSeconds((prevState) => {
                    if (prevState > 1) {
                        return (prevState - 1);
                    } else {
                        clearInterval(countDownTimer);
                        return 0
                    }
                })
            }, 1000);
        }

        return (() => { clearInterval(countDownTimer); })

    }, [sendCodeMoment]);

    useEffect(() => {
        if (!showVerificationFormDelayed) {
            setTimeout(() => { setShowVerificationForm(false) }, 200);
        }
    }, [showVerificationFormDelayed]);


    useEffect(() => {
        if (showVerificationForm) {
            setTimeout(() => { setShowVerificationFormDelayed(true) }, 100);
        }
    }, [showVerificationForm]);

    const dispatch = useAppDispatch();

    const sendOtpCode = async (phoneNumber: string) => {

        setLoading(true);
        try {

            const response: any = await sendOtp({ emailOrPhoneNumber: phoneNumber });
            if (response.message) {
                dispatch(setReduxError({
                    title: t('error'),
                    message: response.message,
                    isVisible: true
                }));
            }
            setLoading(false);

            if (response.status == 200) {
                setRemaindSeconds(80);
                setTimeout(() => { setSendCodeMoment(new Date().getTime()) }, 200)
            }

            if (response.status == 500)

                dispatch(setReduxError({
                    title: t('error'),
                    message: response?.data?.error?.message,
                    isVisible: true
                }));

        } catch (error) {
            setLoading(false);
            dispatch(setReduxError({
                title: t('error'),
                isVisible: true
            }))
        }
    }

    const submitHandler = async (values: {
        phoneNumber: string;
    }) => {
        setSavedPhoneNumber(values.phoneNumber);
        setShowVerificationForm(true);
        sendOtpCode(values.phoneNumber);
    }

    const onSuccessLogin = (response: any) => {
        if (response && response.status === 200) {

            const token = response.data?.result?.accessToken
            localStorage.setItem('Token', token);
            props.onCloseLogin();

            dispatch(setReduxUser({
                isAuthenticated: true,
                user: response.data?.result?.user,
                getUserLoading: false
            }));

            const userFirstName = response.data?.result?.user?.firstName || "کاربر";

            dispatch(setReduxNotification({
                status: 'success',
                message: userFirstName +'  عزیز،  خوش آمدید.',
                isVisible: true
            }));            

        } else {
            dispatch(setReduxUser({
                isAuthenticated: false,
                user: {},
                getUserLoading: false
            }));
        }
    }

    const registerOtp = async (code: string) => {
        if (code && savedPhoneNumber && code.length === 6) {
            setRegisterLoading(true);

            dispatch(setReduxUser({
                isAuthenticated: false,
                user: {},
                getUserLoading: true
            }));

            const response: any = await registerOrLogin({ code: code, emailOrPhoneNumber: savedPhoneNumber });

            setRegisterLoading(false);
            if (response.status == 200) {
                onSuccessLogin(response);
            } else {
                let message = "";
                if (response?.response?.data?.error?.message) {    
                    message = response.response.data.error.message;
                }
                dispatch(setReduxNotification({
                    status: 'error',
                    message: message,
                    isVisible: true
                }));
                dispatch(setReduxUser({
                    isAuthenticated: false,
                    user: {},
                    getUserLoading: false
                }));

            }
        }
    }

    return (
        <>
            <Formik
                validate={() => { return {} }}
                initialValues={{ phoneNumber: "" }}
                onSubmit={submitHandler}
            >
                {({ errors, touched, setFieldValue, values }) => {
                    return (

                        <Form className='p-5' autoComplete='off' >

                            <PhoneInput
                                labelIsSimple
                                showRequiredStar
                                defaultCountry={
                                    {
                                        countryCode: "ir",
                                        dialCode: "98",
                                        format: "... ... ...."
                                    }
                                }
                                onChange={(v: string) => {
                                    //setError(false);
                                    setFieldValue('phoneNumber', v)
                                }}
                                name='phoneNumber'
                                isTouched={touched.phoneNumber}
                                label={"شماره موبایل"}
                                errorText={errors.phoneNumber}
                                className="mb-5"
                            />

                            <Button
                                type="submit"
                                className="h-12 w-full"
                                loading={loading}
                            >
                                تایید و دریافت کد
                            </Button>

                        </Form>
                    )
                }}
            </Formik>

            <ModalPortal
                show={showVerificationForm}
                selector='modal_portal_2'
            >
                <div className='fixed top-0 left-0 h-screen w-screen'>
                    <div className='absolute left-0 right-0 top-0 bottom-0 bg-black/75 backdrop-blur' onClick={() => { setShowVerificationFormDelayed(false) }} />

                    <div className={`fixed sm:absolute sm:rounded-md flex flex-col gap-4 items-center top-0 left-0 sm:top-1/2 sm:left-1/2 sm:-translate-y-1/2 sm:-translate-x-1/2 max-h-screen overflow-y-auto p-10 w-screen h-screen sm:h-auto sm:w-520 bg-white duration-200 transition-all ${showVerificationFormDelayed ? "scale-100 opacity-100" : "scale-90 opacity-0"}`} >
                    <button
                        type='button'
                        className='absolute top-3 left-3 sm:hidden'
                        onClick={() => { setShowVerificationFormDelayed(false) }}
                    >
                        <Close className='w-8 h-8 fill-neutral-400' />
                    </button>

                        <Image
                            src="data:image/png;base64,"
                            className='h-16 w-16 block'
                            width={64}
                            height={64}
                            alt="phone icon"
                        />
                        <p className='vlock text-2xl'> کد تایید را وارد کنید </p>

                        <p className='text-xs text-neutral-500'>
                            کد ۶ رقمی به شماره
                            <b className='font-semibold text-sm mx-1' dir="ltr"> {savedPhoneNumber?.replace("+98", "0")} </b>
                            ارسال شد.
                        </p>

                        <button className='text-blue-700 text-sm' onClick={() => {setShowVerificationFormDelayed(false)}}>
                            اصلاح شماره موبایل
                        </button>

                        <OtpInput
                            onChange={(e: string) => {
                                if (!enteredCode && e) {
                                    registerOtp(persianNumbersToEnglish(e));
                                }
                                setEnteredCode(persianNumbersToEnglish(e));
                            }}
                        />

                        {savedPhoneNumber && remaindSeconds === 0 ? (
                            <button
                                type='button'
                                className='mb-4 text-sm'
                                onClick={() => { sendOtpCode(savedPhoneNumber) }}
                            >
                                ارسال مجدد کد
                            </button>
                        ) : loading ? (

                            <Skeleton className='w-24 mt-1 mb-4' />

                        ) : (
                            <div className='mb-4 text-sm'>
                                <CountDown
                                    seconds={remaindSeconds}
                                    simple
                                />
                                <span className='rtl:mr-3 ltr:ml-3'> تا درخواست مجدد کد </span>
                            </div>
                        )}

                        <Button
                            type='button'
                            className='h-12 w-full sm:w-44 shrink-0'
                            onClick={() => { registerOtp(enteredCode) }}
                        >
                            تایید و ادامه
                        </Button>

                        <button
                            type='button'
                            onClick={props.onBackToLoginWithPassword}
                            className='text-sm text-blue-700 hover:text-blue-600'
                        >
                            ورود با کلمه عبور
                        </button>

                    </div>

                </div>

            </ModalPortal>

        </>
    )
}

export default OTPLogin;